import React, { useState } from "react";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import MobileStepper from "@material-ui/core/MobileStepper";
import Button from "@material-ui/core/Button";
import KeyboardArrowLeft from "@material-ui/icons/KeyboardArrowLeft";
import KeyboardArrowRight from "@material-ui/icons/KeyboardArrowRight";
import SwipeableViews from "react-swipeable-views";
import { autoPlay } from "react-swipeable-views-utils";
import Accordion from "@material-ui/core/Accordion";
import AccordionSummary from "@material-ui/core/AccordionSummary";
import AccordionDetails from "@material-ui/core/AccordionDetails";
import Typography from "@material-ui/core/Typography";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
const AutoPlaySwipeableViews = autoPlay(SwipeableViews);

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  header: {
    display: "flex",
    alignItems: "center",
    height: 50,
    paddingLeft: theme.spacing(4),
    backgroundColor: theme.palette.background.default,
  },
}));

function SwipeableTextMobileStepper() {
  const [deals, setDeals] = useState([]);
  const classes = useStyles();
  const theme = useTheme();
  const [activeStep, setActiveStep] = React.useState(0);
  const maxSteps = deals.length;
  React.useEffect(() => {
    (async () => {
      fetch("http://localhost:8080/Assignment3_war/deals")
        .then((response) => response.json())
        .then((data) => {
          setDeals(data);
          console.log(data);
        })
        .catch((err) => console.log(err));
    })();
  }, []);

  const handleNext = () => {
    setActiveStep((prevActiveStep) =>
      prevActiveStep + 1 < deals.length ? prevActiveStep + 1 : 0
    );
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) =>
      prevActiveStep - 1 >= 0 ? prevActiveStep - 1 : deals.length - 1
    );
  };

  const handleStepChange = (step) => {
    setActiveStep(step);
  };

  return (
    <div className={classes.root}>
      <AutoPlaySwipeableViews
        axis={theme.direction === "rtl" ? "x-reverse" : "x"}
        index={activeStep}
        onChangeIndex={handleStepChange}
        enableMouseEvents
      >
        {deals.map((step, index) => (
          <div
            style={{
              height: 180,
              backgroundColor: "white",
              flexDirection: "column",
              width: "100%",
              flex: 1,
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            <div
              style={{
                fontSize: 30,
                color: "#5555ff",
                fontWeight: "bold",
                marginLeft: "15%",
              }}
            >
              <span>
                {step.departureCity} ✈ {step.arrivalCity}
              </span>
              <span style={{ fontSize: 20 }}> 🕗 {step.time}</span>
            </div>
            <div
              style={{
                fontSize: 55,
                color: "#55ff55",
                fontWeight: "bold",
                paddingTop: 10,
                marginLeft: "16%",
              }}
            >
              Flat {step.perc}% Off
              <p style={{ fontSize: 20, color: "blue", marginLeft: "20%" }}>
                (upto Rs {step.cash})
              </p>
            </div>
          </div>
        ))}
      </AutoPlaySwipeableViews>
      <MobileStepper
        steps={maxSteps}
        position="static"
        variant="text"
        activeStep={activeStep}
        nextButton={
          <Button size="small" onClick={handleNext}>
            Next
            {theme.direction === "rtl" ? (
              <KeyboardArrowLeft />
            ) : (
              <KeyboardArrowRight />
            )}
          </Button>
        }
        backButton={
          <Button size="small" onClick={handleBack}>
            {theme.direction === "rtl" ? (
              <KeyboardArrowRight />
            ) : (
              <KeyboardArrowLeft />
            )}
            Back
          </Button>
        }
      />
      <Accordion>
        <AccordionSummary
          expandIcon={<ExpandMoreIcon />}
          aria-controls="panel1a-content"
          id="panel1a-header"
        >
          <Typography className={classes.heading}>View Full List</Typography>
        </AccordionSummary>
        <AccordionDetails>
          <div className={classes.root}>
            {deals.map((step, index) => (
              <div
                style={{
                  height: 180,
                  backgroundColor: "white",
                  flexDirection: "column",
                  width: "100%",
                  flex: 1,
                  justifyContent: "center",
                  alignItems: "center",
                }}
              >
                <div
                  style={{
                    fontSize: 30,
                    color: "#5555ff",
                    fontWeight: "bold",
                    marginLeft: "15%",
                  }}
                >
                  <span>
                    {step.departureCity} ✈ {step.arrivalCity}
                  </span>
                  <span style={{ fontSize: 20 }}> 🕗 {step.time}</span>
                </div>
                <div
                  style={{
                    fontSize: 55,
                    color: "#55ff55",
                    fontWeight: "bold",
                    paddingTop: 10,
                    marginLeft: "16%",
                  }}
                >
                  Flat {step.perc}% Off
                  <p style={{ fontSize: 20, color: "blue", marginLeft: "20%" }}>
                    (upto Rs {step.cash})
                  </p>
                </div>
              </div>
            ))}
          </div>
        </AccordionDetails>
      </Accordion>
    </div>
  );
}

export default SwipeableTextMobileStepper;
